import React, { useEffect } from 'react';
import { useParams } from "react-router-dom";
import { Link } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import axios from 'axios';

const EditProduct = (props) => {
    const { id } = useParams();


 const [img, setImg] = React.useState(null)
 //const [imgVal, setImgVal] = React.useState(null)
    const [selectedFile, setSelectedFile] = React.useState(null);

    const { register, handleSubmit, setValue } = useForm();

    useEffect(() => {


      axios.get(`https://fakestoreapi.com/products/${id}`).then(result => {


            setValue("title", result.data.title);
           setValue("price", result.data.price);
            setValue("description", result.data.description);
            setValue("image", result.data.image);
            setValue("category", result.data.category);

            setValue("body", result.data.body);


        })
    }, [id, setValue]);


    const onSubmit = async (data) => {
        console.log(data);
        console.log(id);

        const formData = new FormData();
        formData.append('title',data.title);
        formData.append('price',data.price);
        formData.append('description',data.description);
        formData.append('image',data.image);
        formData.append('category',data.category);



        axios.put(`https://fakestoreapi.com/products/${id}`, formData).then(result => {
            console.log(result);
            props.history.push("/products");
        }).catch((e) => console.log(e.message))
    };

    return (

      <Paper sx={{ p: 2, margin: 'auto', maxWidth: 1000, flexGrow: 1 }}>
      product id :{prod?.id}

          <Grid container spacing={2}>
            <Grid item>
              <ButtonBase sx={{ width: 128, height: 128 }}>
                <Img alt="complex" src={prod?.image} />
              </ButtonBase>
            </Grid>
            <Grid item xs={12} sm container>
              <Grid item xs container direction="column" spacing={2}>
                <Grid item xs>
                  <Typography gutterBottom variant="subtitle1" component="div">
                  {prod?.title}
                  </Typography>
                  <Typography variant="body2" gutterBottom>
                  {prod?.category}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                  {prod?.description}
                  </Typography>
                </Grid>
                <Grid item>
                  <Typography sx={{ cursor: 'pointer' }} variant="body2">
                    <Rating
            name="simple-controlled"
            value={Number(prod?.rating.rate)}
            /> 
             {prod?.rating.count} 



                  </Typography>
                </Grid>
              </Grid>
              <Grid item>
                <Typography variant="subtitle1" component="div">

                {prod?.price}$
                </Typography>
              </Grid>
            </Grid>
          </Grid> 
        </Paper>
    );
}

export default EditProduct;
