import React,{useEffect,useState} from 'react'
import {useParams} from "react-router-dom"
import axios from "axios";
import {useHistory} from "react-router-dom"
import { FormLabel, Input } from '@mui/material';

const EditProduct = () => {
    const history=useHistory()
    const {id}=useParams()
    const [title,setTitle]=useState("")
    const [price,setPrice]=useState("");
    const [img,setImage]=useState("");
    const [category,setCategory]=useState("");
    useEffect(()=>{
        axios.get(`https://fakestoreapi.com/products/${id}`).then(res => {

         console.log(res);
         setTitle(res.data.title)
            setPrice(res.data.price)
            setImage(res.data.image)
            setCategory(res.data.category)
        }).catch(err => console.log(err))
    },[id])

    const handleSubmit=(event)=>{
        event.preventDefault();
        if(title === "" || price === "" || img === "" || category === "") return
        const updatedMovie={
            id,
            title,
            price,
            imageURL:img,
            category
        }
        axios.put(`https://fakestoreapi.com/products/${id}`,updatedMovie).then(res => history.push("/products")).catch(err => console.log(err))

    }
    return (
        <div className="container">
                    <form className="mt-5" onSubmit={handleSubmit}>Update  Form

            <div className="form-row">
                <div className="form-group col-md-10">
                    <FormLabel htmlFor="inputName">Name</FormLabel>
                    <Input  type="text" 
                            className="form-control" 
                            name="name"
                            value={title}
                            onChange={(e)=> setTitle(e.target.value)}
                           />
                </div>
                <div className="form-group col-md-2">
                    <label htmlFor="inputRating">Rating</label>
                    <Input 
                            type="text" 
                            className="form-control" 
                            name="rating"
                            value={price}
                            onChange={(e)=> setPrice(e.target.value)}
                          />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-12">
                    <label htmlFor="inputImage">Image URL</label>
                    <Input 
                            type="text" 
                            className="form-control" 
                            name="imageURL"
                            value={img}
                            onChange={(e)=> setImage(e.target.value)}
                           />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-12">
                    <label htmlFor="overviewTextarea">Overview</label>
                    <Textarea 
                            className="form-control" 
                            name="overview" rows="5"
                            value={category}
                            onChange={(e)=> setCategory(e.target.value)}
                           ></Textarea>
                </div>
            </div>
            <input type="submit" className="btn btn-danger btn-block" value="Edit Movie" />
        </form>

    </div>
    )
}

export default EditProduct
