import React,{useEffect,useState} from 'react'
import {useParams} from "react-router-dom"
import axios from "axios";
import {useHistory} from "react-router-dom"
import { FormLabel, Input, Paper } from '@mui/material';

const EditProduct = () => {
    const history=useHistory()
    const {id}=useParams()
    const [title,setTitle]=useState("")
    const [price,setPrice]=useState("");
    const [img,setImage]=useState("");
    const [category,setCategory]=useState("");
    useEffect(()=>{
        axios.get(`https://fakestoreapi.com/products/${id}`).then(res => {

         console.log(res);
         setTitle(res.data.title)
            setPrice(res.data.price)
            setImage(res.data.image)
            setCategory(res.data.category)
        }).catch(err => console.log(err))
    },[id])

    const handleSubmit=(event)=>{
        event.preventDefault();
        if(title === "" || price === "" || img === "" || category === "") return
        const updatedMovie={
            id,
            title,
            price,
            imageURL:img,
            category
        }
        axios.put(`https://fakestoreapi.com/products/${id}`,updatedMovie).then(res => history.push("/products")).catch(err => console.log(err))

    }
    return (
      <Paper sx={{ p: 2, margin: 'auto', maxWidth: 1000, flexGrow: 1 }}>
       <div className='container mt-3'>
        <div className="card">
        <h5 className="card-header">Mise à jour </h5>



                <div className="form-group">
                    <form   onSubmit={handleSubmit}>

            <div className="form-row">
                <div className="form-group col-md-10">
                    <FormLabel  style={{marginRight:'20px'}}>Title</FormLabel>
                    <Input  type="text" 
                            className="form-control" 
                            name="title"
                            value={title}
                            onChange={(e)=> setTitle(e.target.value)}
                           />
                </div>
                <div className="form-group col-md-2">
                    <FormLabel  style={{marginRight:'20px'}}>price</FormLabel>
                    <Input 
                            type="text" 
                            className="form-control" 
                            name="price"
                            value={price}
                            onChange={(e)=> setPrice(e.target.value)}
                          />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-12">
                    <FormLabel  style={{marginRight:'20px'}} >Image URL</FormLabel>
                    <Input 
                            type="text" 
                            className="form-control" 
                            name="imageURL"
                            value={img}
                            onChange={(e)=> setImage(e.target.value)}
                           />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-12">
                    <FormLabel  style={{marginRight:'20px'}}>Category</FormLabel>
                    <Input 

                            className="form-control" 
                            name="category" 
                            value={category}
                            onChange={(e)=> setCategory(e.target.value)}
                           ></Input>
                </div>
            </div>
            <input type="submit" className="btn btn-danger btn-block" value="Edit Movie" />
        </form>
        </div>
        </div>

        </div>

    </Paper>
    )
}

export default EditProduct
