import React,{useEffect,useState} from 'react'
import {useParams} from "react-router-dom"
import axios from "axios";
import {useHistory} from "react-router-dom"

const EditProduct = () => {
    const history=useHistory()
    const {id}=useParams()
    const [title,setName]=useState("")
    const [rating,setRating]=useState("");
    const [img,setImage]=useState("");
    const [overview,setOverview]=useState("");
    useEffect(()=>{
        axios.get(`https://fakestoreapi.com/products/${id}`).then(res => {

         console.log(res);
            setName(res.data.title)
            setRating(res.data.price)
            setImage(res.data.image)
            setOverview(res.data.category)
        }).catch(err => console.log(err))
    },[id])

    const handleSubmit=(event)=>{
        event.preventDefault();
        if(name === "" || rating === "" || img === "" || overview === "") return
        const updatedMovie={
            id,
            name,
            rating,
            imageURL:img,
            overview
        }
        axios.put(`https://fakestoreapi.com/products/${id}`,updatedMovie).then(res => history.push("/products")).catch(err => console.log(err))

    }
    return (
        <div className="container">
                    <form className="mt-5" onSubmit={handleSubmit}>
        <input className="form-control" id="disabledInput" type="text" placeholder="Edit To Form To Update.." disabled/>
            <div className="form-row">
                <div className="form-group col-md-10">
                    <label htmlFor="inputName">Name</label>
                    <input  type="text" 
                            className="form-control" 
                            name="name"
                            value={name}
                            onChange={(e)=> setName(e.target.value)}
                           />
                </div>
                <div className="form-group col-md-2">
                    <label htmlFor="inputRating">Rating</label>
                    <input 
                            type="text" 
                            className="form-control" 
                            name="rating"
                            value={rating}
                            onChange={(e)=> setRating(e.target.value)}
                          />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-12">
                    <label htmlFor="inputImage">Image URL</label>
                    <input 
                            type="text" 
                            className="form-control" 
                            name="imageURL"
                            value={img}
                            onChange={(e)=> setImage(e.target.value)}
                           />
                </div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-12">
                    <label htmlFor="overviewTextarea">Overview</label>
                    <textarea 
                            className="form-control" 
                            name="overview" rows="5"
                            value={overview}
                            onChange={(e)=> setOverview(e.target.value)}
                           ></textarea>
                </div>
            </div>
            <input type="submit" className="btn btn-danger btn-block" value="Edit Movie" />
        </form>

    </div>
    )
}

export default EditProduct
